<?php
	$msg="";
	$result="";
	$status="";
	if(isset($_POST['submit'])){	
		$mobile1= $_POST['mobile'];
		$contcode= $_POST['contcode'];
		$mobile=$contcode."".$mobile1;
		
		$url="http://apilayer.net/api/validate?access_key=92affd0391ca47c1f206ec514bfb7581&number=$mobile";
		$ch=curl_init();
		curl_setopt($ch,CURLOPT_URL,$url);
		curl_setopt($ch,CURLOPT_RETURNTRANSFER,true);
		$result=curl_exec($ch);
		curl_close($ch);
		$result= json_decode($result,true);
		if($result['valid']==1){
			$status="yes";
		} 
		else{
			$msg="No data found";
		}
	}
	/* echo '<pre>';
	print_r($result); */
?>

